'use strict';
var nums = {
   '०':0,
   '१':1,
   '२':2,
   '३':3,
   '४':4,
   '५':5,
   '६':6,
   '७':7,
   '८':8,
   '९':9
};

module.exports = function (strNum) {
  var arrNumNe = strNum.toString().split('').map(function (ch) {
    if (ch === '.' || ch === ',') {
      return ch;
    }
    return nums[Number(ch)];
  });
  return arrNumNe.join('');
};
